import "./App.css";
import React from "react";

// function updateCart() {
//   return (

//   );
// }

// export default updateCart;

// const cartNav

let maxQty = 1;

class Cart extends React.Component {
  constructor(props) {
    super(props); // these first two lines are boilerplate

    this.state = {
      // initial state
      cartName: "Cart",
      quantity: [
        {
          text: "groceries",
          done: true,
          qty: 0,
        },
      ],
    };
  }

  // ALWAYS USE ARROW FUNCTION UNLESS YOU KNOW WHAT YOU ARE DOING!!
  addItem = () => {
    const quantity = this.state.quantity;
    const new_item = [
      ...quantity,
      { text: this.state.input_contents, qty: maxQty },
    ];
    this.setState({ ...this.state, qty: new_item });
    maxQty = maxQty + 1;
  };
}

export default Cart;
