import "./App.css";
import couch1 from "./Assets/couch1.png";
import bed1Y from "./Assets/bed1Y.png";
import round1 from "./Assets/round1.png";
import pouf1Y from "./Assets/pouf1Y.png";
import React, { Component } from "react";

const CarouselImgs = [couch1, bed1Y, round1, pouf1Y];

const Arrow = ({ direction, clickFunction, glyph }) => (
  <div className={direction} onClick={clickFunction}>
    {glyph}
  </div>
);

// setting initial state
export default class Carousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentImgID: 0,
    };
  }

  // change color name and images based on which color is selected
  nextSlide = () => {
    const lastID = CarouselImgs.length - 1;
    const { currentImgID } = this.state;
    const shouldReset = currentImgID === 0;
    const id = shouldReset ? lastID : currentImgID - 1;
    this.setState({
      currentImgID: id,
    });
  };

  lastSlide = () => {
    const lastID = CarouselImgs.length - 1;
    const { currentImgID } = this.state;
    const shouldReset = currentImgID === lastID;
    const id = shouldReset ? lastID : currentImgID + 1;
    this.setState({
      currentImgID: id,
    });
  };

  render() {
    return (
      <div className="carousel">
        <Arrow
          direction="left"
          clickFunction={this.lastSlide}
          glyph="&#9664;"
        />
        <img
          src={CarouselImgs[this.state.currentImgID]}
          alt="Bright Fluff Stuff pillow option"
        ></img>
        <Arrow
          direction="right"
          clickFunction={this.nextSlide}
          glyph="&#9654;"
        />
      </div>
    );
  }
}
