import "./App.css";
import React, { Component } from "react";
import couch1 from "./Assets/couch1.png";
import couch2 from "./Assets/couch2.png";
import couch3 from "./Assets/couch3.png";
import couch4 from "./Assets/couch4.png";
import couch1Y from "./Assets/couch1.png";
import couch2Y from "./Assets/couch2.png";
import couch3Y from "./Assets/couch3.png";
import couch4Y from "./Assets/couch4.png";
import couch1W from "./Assets/couch1.png";
import couch2W from "./Assets/couch2.png";
import couch3W from "./Assets/couch3.png";
import couch4W from "./Assets/couch4.png";
import couch1B from "./Assets/couch1.png";
import couch2B from "./Assets/couch2.png";
import couch3B from "./Assets/couch3.png";
import couch4B from "./Assets/couch4.png";

let currentColor = "Cozy Denim";
const defaultColor = "Cozy Denim";

const imagesPath = {
  blue: {
    one: couch1,
    two: couch2,
    three: couch3,
    four: couch4,
  },
  yellow: {
    one: couch1Y,
    two: couch2Y,
    three: couch3Y,
    four: couch4Y,
  },
  white: {
    one: couch1W,
    two: couch2W,
    three: couch3W,
    four: couch4W,
  },
  brown: {
    one: couch1B,
    two: couch2B,
    three: couch3B,
    four: couch4B,
  },
};

class ColorPicker extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentColor: defaultColor,
      items: [
        {
          id: 1,
          color: "Cozy Denim",
          expanded: true,
          bg: "blue",
          imgs: imagesPath.blue,
        },
        {
          id: 2,
          color: "After School Special",
          expanded: false,
          bg: "yellow",
          imgs: imagesPath.yellow,
        },
        {
          id: 3,
          color: "Rainy Day",
          expanded: false,
          bg: "white",
          imgs: imagesPath.white,
        },
        {
          id: 4,
          color: "Morning Haze",
          expanded: false,
          bg: "brown",
          imgs: imagesPath.brown,
        },
      ],
    };
  }

  // select and deselect
  // one and only one will always be selected
  handleToggle = (id) => {
    console.log(id);
    const newCurrentColor = this.state.items[id - 1].color;
    const updatedItems = this.state.items.map((item) => {
      if (item.id === id && item.expanded === false) {
        return {
          ...item,
          expanded: item.id === id,
        };
      }
      if (item.expanded === true && item.id !== id) {
        return {
          ...item,
          expanded: item.id === id,
        };
      } else {
        return item;
      }
    });
    this.setState({
      ...this.state,
      items: updatedItems,
      currentColor: newCurrentColor,
    });
  };

  render() {
    return this.state.items.map((el) => (
      <div>
        <h6>Color: {this.state.currentColor} </h6>
        <div
          key={el.id}
          onClick={() => this.handleToggle(el.id)}
          className={[
            el.bg,
            "colorOption",
            el.expanded ? "selected" : "unselected",
          ].join(" ")}
        />
      </div>
    ));
  }
}

export { ColorPicker, currentColor };
