//   },
// };

class ColorPicker extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentColor: currentColor,
      // items: [
      // {
      //   id: 0,
      //   color: "Cozy Denim",
      //   expanded: true,
      //   bg: "blue",
      //   imgs: imagesPath.blue,
      // },
      // {
      //   id: 1,
      //   color: "After School Special",
      //   expanded: false,
      //   bg: "yellow",
      //   imgs: imagesPath.yellow,
      // },
      // {
      //   id: 2,
      //   color: "Rainy Day",
      //   expanded: false,
      //   bg: "white",
      //   imgs: imagesPath.white,
      // },
      // {
      //   id: 3,
      //   color: "Morning Haze",
      //   expanded: false,
      //   bg: "brown",
      //   imgs: imagesPath.brown,
      // },
      // ],
    };

    // this.state.items = props.colorList.map(color => {...Colors, expanded: false}),
    // this.state.items = props.fillList.map(fill => {...fill, expanded: false});
  }

  // select and deselect
  // one and only one will always be selected
  handleToggle = (id) => {
    const newCurrentColor = this.state.items[id].color;
    const newImagesPath = this.state.items[id].imgs;
    const updatedItems = this.state.items.map((item) => {
      if (item.id === id && item.expanded === false) {
        return {
          expanded: item.id === id,
        };
      }
      if (item.expanded === true && item.id !== id) {
        return {
          expanded: item.id === id,
        };
      } else {
        return item;
      }
    });
    this.setState({
      ...this.state,
      items: updatedItems,
      currentColor: newCurrentColor,
      imagesPath: newImagesPath,
    });
    this.props.onColorChange(newCurrentColor);
  };

  render() {
    return (
      <div>
        <div
          key={el.id}
          onClick={() => this.handleToggle(el.id)}
          className={[
            el.bg,
            "colorOption",
            el.expanded ? "selected" : "unselected",
          ].join(" ")}
        />
      </div>
    );
  }
}

export { ColorPicker, currentColor };
