import "./App.css";
import React, { Component } from "react";

export default class ColorPicker extends Component {
  state = {
    items: [
      { id: 1, color: "Cozy Denim", expanded: true },
      { id: 2, color: "After School Special", expanded: false },
      { id: 3, color: "Rainy Day", expanded: false },
      { id: 4, color: "Morning Haze", expanded: false },
    ],
  };
  // select or deselect
  handleToggle = (id) => {
    const updatedItems = this.state.items.map((item) => {
      if (item.id === id) {
        return {
          ...item,
          expanded: !item.expanded,
        };
      } else {
        return item;
      }
    });

    this.setState({ ...this.state, items: updatedItems });
  };

  render() {
    return this.state.items.map((el) => (
      <div
        key={el.id}
        onClick={() => this.handleToggle(el.id)}
        className={el.expanded ? "selected" : "unselected"}
      >
        {/* <p className="mb-0">
          <strong>{el.name}</strong>
          <span> {el.expanded.toString()}</span>
        </p> */}
      </div>
    ));
  }
}

