import "./App.css";
import React, { Component } from "react";

let currentColor;

class ColorPicker extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        {
          id: 1,
          color: "Cozy Denim",
          expanded: true,
          bg: "blue",
        },
        {
          id: 2,
          color: "After School Special",
          expanded: false,
          bg: "yellow",
        },
        {
          id: 3,
          color: "Rainy Day",
          expanded: false,
          bg: "white",
        },
        {
          id: 4,
          color: "Morning Haze",
          expanded: false,
          bg: "brown",
        },
      ],
    };
  }

  // select and deselect
  // one and only one will always be selected
  handleToggle = (id) => {
    let currentColor = this.item.color;
    const updatedItems = this.state.items.map((item) => {
      if (item.id === id && item.expanded === false) {
        return {
          ...item,
          expanded: !item.expanded,
        };
      }
      if (item.expanded === true && item.id !== id) {
        return {
          ...item,
          expanded: !item.expanded,
        };
      } else {
        return item;
      }
    });
    this.setState({ ...this.state, items: updatedItems });
  };

  render() {
    return this.state.items.map((el) => (
      <div>
        <h6>Color: {currentColor}</h6>
        <div
          key={el.id}
          onClick={() => this.handleToggle(el.id)}
          className={[
            el.bg,
            "colorOption",
            el.expanded ? "selected" : "unselected",
          ].join(" ")}
        />
      </div>
    ));
  }
}

export default ColorPicker;
