import "./App.css";
import React, { Component } from "react";

let currentFill = "Select a fill";

class FillPicker extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        {
          id: 1,
          fill: "Duck Down",
          expanded: true,
        },
        {
          id: 2,
          fill: "Memory Foam",
          expanded: false,
        },
        {
          id: 3,
          fill: "Hypoallergenic Poly-Blend",
          expanded: false,
        },
      ],
    };
  }

  // select and deselect
  // one and only one will always be selected
  handleToggle = (id) => {
    const updatedItems = this.state.items.map((item) => {
      if (item.id === id && item.expanded === false) {
        currentFill = item.fill;
        return {
          ...item,
          expanded: !item.expanded,
        };
      }
      if (item.expanded === true && item.id !== id) {
        return {
          ...item,
          expanded: !item.expanded,
        };
      } else {
        return item;
      }
    });
    this.setState({
      ...this.state,
      items: updatedItems,
    });
  };

  render() {
    return this.state.items.map((al) => (
      <div>
        <h6>Fill: {currentFill} </h6>
        <div
          key={al.id}
          onClick={() => this.handleToggle(al.id)}
          className={[
            "fillOption",
            al.expanded ? "selected" : "unselected",
          ].join(" ")}
        />
      </div>
    ));
  }
}

export { FillPicker, currentFill };
