import "./App.css";
import React, { Component } from "react";

const currentColor = "Cozy Denim";

class ColorPicker extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentColor: currentColor,
      items: [
        {
          id: 0,
          expanded: true,
        },
        {
          id: 1,
          expanded: false,
        },
        {
          id: 2,
          expanded: false,
        },
        {
          id: 3,
          expanded: false,
        },
      ],
    };

    this.state.items = props.colorList.map((color) => {
      color;
    });
    // this.state.items = props.fillList.map(fill => {...fill, expanded: false});
  }

  // select and deselect
  // one and only one will always be selected
  handleToggle = (id) => {
    const newCurrentColor = this.state.items[id].color;
    const newImagesPath = this.state.items[id].imgs;
    const updatedItems = this.state.items.map((item) => {
      if (item.id === id && item.expanded === false) {
        return {
          expanded: item.id === id,
        };
      }
      if (item.expanded === true && item.id !== id) {
        return {
          expanded: item.id === id,
        };
      } else {
        return item;
      }
    });
    this.setState({
      ...this.state,
      items: updatedItems,
      currentColor: newCurrentColor,
      imagesPath: newImagesPath,
    });
    this.props.onColorChange(newCurrentColor);
  };

  render() {
    return this.state.items.map((el) => (
      <div>
        <div
          key={el.id}
          onClick={() => this.handleToggle(el.id)}
          className={[
            "colorOption",
            el.expanded ? "selected" : "unselected",
          ].join(" ")}
        />
      </div>
    ));
  }
}

export { ColorPicker, currentColor };
