import "./App.css";
import React, { Component } from "react";

const currentFill = "Select a fill";

class FillPicker extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentFill: currentFill,
      items: [
        {
          id: 0,
          fill: "Duck Down",
          expanded: false,
        },
        {
          id: 1,
          fill: "Memory Foam",
          expanded: false,
        },
        {
          id: 2,
          fill: "Hypoallergenic Poly-Blend",
          expanded: false,
        },
      ],
    };
  }

  // select and deselect
  // one and only one will always be selected
  handleToggle = (id) => {
    const newCurrentFill = this.state.items[id].fill;
    const updatedItems = this.state.items.map((item) => {
      if (item.id === id && item.expanded === false) {
        return {
          ...item,
          expanded: item.id === id,
        };
      }
      if (item.expanded === true && item.id !== id) {
        return {
          ...item,
          expanded: item.id === id,
        };
      } else {
        return item;
      }
    });
    this.setState({
      ...this.state,
      items: updatedItems,
      currentFill: newCurrentFill,
    });
    this.props.onFillChange(newCurrentFill);
  };

  render() {
    return this.state.items.map((al) => (
      <div>
        <p>{currentFill}</p>
        <div
          key={al.id}
          onClick={() => this.handleToggle(al.id)}
          className={[
            "fillOption",
            al.expanded ? "selected" : "unselected",
          ].join(" ")}
        >
          {al.fill}
        </div>
      </div>
    ));
  }
}

export { FillPicker, currentFill };
