import "./App.css";
import Navbar from "./navbar.js";
import { ColorPicker, currentColor, imagesPath } from "./colorPicker.js";
import { FillPicker, currentFill } from "./fillPicker.js";
import couch1 from "./Assets/couch1.png";
import couch2 from "./Assets/couch2.png";
import couch3 from "./Assets/couch3.png";
import couch4 from "./Assets/couch4.png";
import couch1Y from "./Assets/couch1.png";
import couch2Y from "./Assets/couch2.png";
import couch3Y from "./Assets/couch3.png";
import couch4Y from "./Assets/couch4.png";
import couch1W from "./Assets/couch1.png";
import couch2W from "./Assets/couch2.png";
import couch3W from "./Assets/couch3.png";
import couch4W from "./Assets/couch4.png";
import couch1B from "./Assets/couch1.png";
import couch2B from "./Assets/couch2.png";
import couch3B from "./Assets/couch3.png";
import couch4B from "./Assets/couch4.png";
import React, { Component } from "react";

const Couch = (props) => {
  return <Product id={props.id} />;
};

const Bed = (props) => {
  return <Product id={props.id} />;
};

const Round = (props) => {
  return <Product id={props.id} />;
};

const Pouf = (props) => {
  return <Product id={props.id} />;
};

const Colors = [
  [
    { id: 0, commonName: "Cozy Denim", hex: "#3BACD0" },
    { id: 1, commonName: "After School Special", hex: "#EAC84F" },
    { id: 2, commonName: "Rainy Day", hex: "#F3F4E8" },
    { id: 3, commonName: "Morning Haze", hex: "#84602A" },
  ],
];

// const Fills = [
//   [
//     { id: 0, commonName: "DUCK DOWN" },
//     { id: 1, commonName: "MEMORY FOAM" },
//     { id: 2, commonName: "HYPOALLERGENIC POLY-BLEND" }
//   ]
// ]

const Images = [
  [
    { id: 0, commonName: "Cozy Denim", hex: "#3BACD0" },
    { id: 1, commonName: "After School Special", hex: "#EAC84F" },
    { id: 2, commonName: "Rainy Day", hex: "#F3F4E8" },
    { id: 3, commonName: "Morning Haze", hex: "#84602A" },
  ],
];

const ProductList = [
  {
    id: 0,
    defaultColorID: 0,
    name: "Couch Pillow",
    // images: [
    //   {0: ['couch1', "couch2", "couch3", "couch4"]},
    //   {1: ['couch1Y', "couch2Y", "couch3Y", "couch4Y"]},
    //   {2: ['couch1W', "couch2W", "couch3W", "couch4W"]},
    //   {3: ['couch1B', "couch2B", "couch3B", "couch4B"]},
    // ],
    price: "$87",
  },
  {
    id: 1,
    defaultColorID: 1,
    name: "Bed Pillow",
    // images: [
    //   {0: ['couch1', "couch2", "couch3", "couch4"]},
    //   {1: ['couch1Y', "couch2Y", "couch3Y", "couch4Y"]},
    //   {2: ['couch1W', "couch2W", "couch3W", "couch4W"]},
    //   {3: ['couch1B', "couch2B", "couch3B", "couch4B"]},
    // ],
    price: "$87",
  },
  {
    id: 0,
    defaultColorID: 0,
    name: "Round Pillow",
    // images: [
    //   {0: ['couch1', "couch2", "couch3", "couch4"]},
    //   {1: ['couch1Y', "couch2Y", "couch3Y", "couch4Y"]},
    //   {2: ['couch1W', "couch2W", "couch3W", "couch4W"]},
    //   {3: ['couch1B', "couch2B", "couch3B", "couch4B"]},
    // ],
    price: "$87",
  },
  {
    id: 0,
    defaultColorID: 0,
    name: "Floor Pouf Pillow",
    // images: [
    //   {0: ['couch1', "couch2", "couch3", "couch4"]},
    //   {1: ['couch1Y', "couch2Y", "couch3Y", "couch4Y"]},
    //   {2: ['couch1W', "couch2W", "couch3W", "couch4W"]},
    //   {3: ['couch1B', "couch2B", "couch3B", "couch4B"]},
    // ],
    price: "$87",
  },
];

export default class Product extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: ProductList[props.id],
      currentColor: ProductList[props.defaultColorID],
    };
  }

  // export default class Couch extends Component {
  //   constructor(props) {
  //     super(props);
  //     this.state = {
  //       currentColor:
  //         currentColor /* ProductList[props.productID].colors[0].color*/,
  //       currentFill:
  //         currentFill /* ProductList[props.productID].colors[0].color*/,
  //       imagesPath: imagesPath,
  //     };
  //   }

  render() {
    console.log(this.state.imagesPath);
    let simpleColorName = null;
    if (this.state.currentColor === "Cozy Denim") {
      simpleColorName = "blue";
    } else if (this.state.currentColor === "Cozy Denim") {
    }

    return (
      <div>
        <header>
          <Navbar />
        </header>
        <body className="detailPage">
          {/* left hand side of page -- images */}
          <div className="detailImgs">
            <img
              className="detailImg"
              src={this.state.imagesPath[simpleColorName][0]}
              alt="Red couch with a bright blue Fluff Stuff pillow"
            ></img>
            <img
              className="detailImg"
              src={this.state.imagesPath.blue[1]}
              alt="Red couch with a bright blue Fluff Stuff pillow"
            ></img>
            <img
              className="detailImg"
              src={this.state.imagesPath.blue[2]}
              alt="Red couch with a bright blue Fluff Stuff pillow"
            ></img>
            <img
              className="detailImg"
              src={this.state.imagesPath.blue[3]}
              alt="Red couch with a bright blue Fluff Stuff pillow"
            ></img>
          </div>
          {/* right hand side of page -- text */}
          {/* product purchase info */}
          <div className="detailText">
            <h3>Couch Pillow</h3>{" "}
            {/* ProductsList[this.props.productID].name */}
            <h5>$89</h5> {/* ProductsList[this.props.productID].price */}
            <h6>Color: {this.state.currentColor}</h6>
            <div className="pillowOptions">
              <ColorPicker /* colors = ProductsList[this.props.productID].colors */
                colorList={Colors}
                currentColor={this.state.currentColor}
                imagesPath={this.state.imagesPath}
                onColorChange={(newCurrentColor, newImagesPath) => {
                  this.setState({
                    ...this.state,
                    currentColor: newCurrentColor,
                    imagesPath: newImagesPath,
                  });
                }}
              />
            </div>
            <h6>Fill: {this.state.currentFill}</h6>
            <div className="pillowOptions">
              <FillPicker /* colors = ProductsList[this.props.productID].colors */
                currentFill={this.state.currentFill}
                onFillChange={(newCurrentFill) => {
                  this.setState({
                    ...this.state,
                    currentFill: newCurrentFill,
                  });
                }}
              />
            </div>
            <div className="miniLink">What fill should I get?</div>
            <button className="filledButton inactive">ADD TO CART</button>
            {/* product description */}
            <div className="descSection">
              <h6 className="miniHeader">PRODUCT DESCRIPTION</h6>
              <p className="descText">
                Delicately fabricated, our signature Couch Pillow will make your
                living room more inviting than ever. Our beautiful colors will
                complement any room and bring out the natural beauty of your
                decor.
              </p>
              <ul className="descList">
                <li>
                  <p className="descBullet">Handmade in Pittsburgh, PA</p>
                </li>
                <li>
                  <p className="descBullet">On-seam closure</p>
                </li>
                <li>
                  <p className="descBullet">Knife-edge tailoring</p>
                </li>
                <li>
                  <p className="descBullet">Yarn-dyed with reactive dyes</p>
                </li>
              </ul>
            </div>
            {/* care instructions */}
            <div className="descSection">
              <h6 className="miniHeader">CARE INSTRUCTIONS</h6>
              <ul className="descList">
                <li>
                  <p className="descBullet">Hand wash only in warm water</p>
                </li>
                <li>
                  <p className="descBullet">Hang to dry</p>
                </li>
                <li>
                  <p className="descBullet">Do not dry clean</p>
                </li>
                <li>
                  <p className="descBullet">Do not bleach</p>
                </li>
                <li>
                  <p className="descBullet">Low iron as needed</p>
                </li>
              </ul>
            </div>
            {/* shipping and returns info */}
            <div className="descSection">
              <h6 className="miniHeader">SHIPPING AND RETURNS</h6>
              <p className="descText">
                Free standard shipping on this item anywhere within the
                contingent United States. Estimated delivery time is within 1-4
                business days. Upgradde to express shipping to receive within
                1-2 business days. Free returns accepted within 30 days.
              </p>
            </div>
            {/* reviews */}
            <div className="descSection">
              <div className="starSection reviewSect">
                <h6 className="miniHeader">REVIEWS</h6>
                {/* overall star rating */}
                <div className="stars">
                  <div className="star"></div>
                  <div className="star"></div>
                  <div className="star"></div>
                  <div className="star"></div>
                </div>
                <h6 className="miniHeader">4.1</h6>
              </div>
              <div className="starSection">
                <h6 className="miniHeader">Jennifer S.</h6>
                <div className="stars">
                  <div className="star"></div>
                  <div className="star"></div>
                  <div className="star"></div>
                  <div className="star"></div>
                  <div className="star"></div>
                </div>
              </div>
              <p className="descText">
                I love this pillow! It's super soft and makes my couch look so
                much more luxurious than it is. I'm going to buy one in every
                color!
              </p>
              <div className="starSection">
                <h6 className="miniHeader">James R.</h6>
                <div className="stars">
                  <div className="star"></div>
                  <div className="star"></div>
                  <div className="star"></div>
                  <div className="star"></div>
                </div>
              </div>
              <p className="descText">
                This pillow is awesome! The quality is really good and I like to
                support small businesses. I had to remove a star because I find
                it difficult to wash. A pillow that I could just toss in the
                washer would be great.
              </p>
              <div className="starSection">
                <h6 className="miniHeader">Julie B.</h6>
                <div className="stars">
                  <div className="star"></div>
                  <div className="star"></div>
                  <div className="star"></div>
                  <div className="star"></div>
                </div>
              </div>
              <p className="descText">
                I love my new couch pillow. Thank you Fluff Stuff. Sadly
                shipping took much longer than expected.
              </p>
              <div className="centerButton">
                <button className="filledButtonMinor">LOAD MORE</button>
              </div>
            </div>
          </div>
        </body>
      </div>
    );
  }
}
